import React,{useState} from 'react'
import {Link} from 'react-router-dom'
import Button from './Button';
import './Navbar.css'
function Navbar() {
    const [click,setClick]=useState(false);
    const [button,setButton]=useState(true)
    const handleClick=()=> setClick(!click);
    const closeMobileMenu= ()=>setClick(false);
    const showButton=()=>{
        if(window.innerWidth<=960){
            setButton(false)
        }
        else{
            setButton(true)
        }
    }
    useEffect(()=>{
        showButton();
    },[]);
    window.addEventListener('resize',showButton)
  return (
    <>
        <nav className="navbar">
            <div className="navbar-container">
            <Link to="/" className="navbar-logo">
                Report Incidents<i class="fa fa-car"></i>
            </Link>
            <div className='menu-icon' onClick={handleClick}>
                <i className={click ? 'fas fa-times':'fas fa-bars'}></i>
            </div>
            <ul className={click ? 'nav-menu active':'nav-menu'}>
                <li className='nav-item'>
                    <Link to='/' className='nav-links' onClick={closeMobileMenu}>Home</Link>
                </li>
                <li className='nav-item'>
                    <Link to='/report' className='nav-links' onClick={closeMobileMenu}>Report</Link>
                </li>
                <li className='nav-item'>
                    <Link to='/view' className='nav-links' onClick={closeMobileMenu}>View</Link>
                </li>
            </ul>
            {button && <Button buttonStyle='btn--outline'>User</Button>}
            </div>
        </nav>
    </>
  )
}

export default Navbar
