import React,{useEffect,useState} from 'react'
import './Search.css'
export default function SearchFilter(){
    const[data,setData]=useState([])
    const[searchApiData,setSearchApiData]=useState([])
    const [filterVal,setFilterVal]=useState('')
    useEffect(()=>{
    const fetchData=()=>{
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => {
                    setData(json)
                    setSearchApiData(json) 
                })
    }
    fetchData();
},[])
const handleFilter=(e)=>
{
    if(e.target.value===''){
        setData(searchApiData)
    }
    else{
        const filterResult=searchApiData.filter(item =>item.incidentType.toLowerCase().includes(e.target.value.toLowerCase())||item.incidentId.toLowerCase().includes(e.target.value.toLowerCase()))
        if(filterResult.length>0){
        setData(filterResult)
        }
        else{
            setData([{"incidentId":"No Records"}])
        }
    }
    setFilterVal(e.target.value)

}

return(
    <div className='Search-container'>
        <div style={{margin:'20px 20%'}}>
            <h1>Incident Search</h1>
            <input type="search" placeholder='Search..' value={filterVal} onInput={(e)=>handleFilter(e)} />
        </div>
        <table>
            <tbody>

            <tr>
            <th>INCIDENT_ID</th>
            <th>DEVICE_ID</th>
            <th>INCIDENT TYPE</th>
            <th>SUB-TYPE</th>
            <th>SEVERITY LEVEL</th>
            <th>DAMAGES</th>
            <th>DESCRIPTION</th>
            <th>Reporting_Time</th>
            <th>UPVOTE(S)</th>
            <th>DOWNVOTE(S)</th>
            <th>COUNT</th>
            </tr>

            {
                data.map(item =>{
                    return(
                        <React.Fragment key={item.incidentId}>
                        <tr>
                            <td>{item.incidentId}</td>
                            <td>{item.deviceId}</td>
                            <td>{item.incidentType}</td>
                            <td>{item.subtype}</td>
                            <td>{item.severityLevel}</td>
                            <td>{item.damages}</td>
                            <td>{item.addInfo}</td>
                            <td>{item.location}</td>
                            <td>{item.timeStamp}</td>
                            <td>{item.upvote}</td>
                            <td>{item.downvote}</td>
                            <td>{item.reportCount}</td>
                        </tr>
                        </React.Fragment>
                    )
                })
            }

            </tbody>
        </table>
    </div>
)
}
