import React,{Component} from 'react';

class Search extends Component
{ 
    constructor(props)
    {

   super(props)

   this.state={
    'op':[],
      str:''

   }
   this.searchHandler=this.searchHandler.bind(this)
   this.handlestr=this.handlestr.bind(this)
}


handlestr=(event)=>
{
this.setState({  
  [event.target.name]:event.target.value
})
}


searchHandler=(event)=>
{  


 if(this.state.str==="Traffic"||this.state.str==="traffic"||this.state.str==="Accident"||this.state.str==="accident"||this.state.str==="Hazard"||this.state.str==="hazard")
 {
  var body={
    str:this.state.str
  }


  const url = "http://localhost:9000/incidents/findbyt/"+this.state.str;
  console.log(body)
  let headers=new Headers();
  headers.append('Content-Type','application/json');
  headers.append('Accept','application/json');
  headers.append('Access-control-Allow-origin',url);
  headers.append('Access-control-Allow-Credentials','true');
  headers.append('POST','GET');
  fetch(url, {
    headers: headers,
    method: 'GET',

  })


  .then(response => response.json()) 
  .then(contents => {
    console.log(contents);
  })

  .catch(() => console.log("can't access " + url + " response. "))
 }


 else{

  const url = "http://localhost:9000/incidents/findbyl/"+this.state.str;

  let headers=new Headers();
  headers.append('Content-Type','application/json');
  headers.append('Accept','application/json');
  headers.append('Access-control-Allow-origin',url);
  headers.append('Access-control-Allow-Credentials','true');
  headers.append('POST','GET');
  fetch(url, {
    headers: headers,
    method: 'GET',

  })


    .then(response => response.json())
    .then(contents => {
      console.log(contents);


    })


    .catch(() => console.log("can't access" + url + "response. "));

 }

}

render()
{
    return(
        <div>
          <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
           <input type="text" name="str" value={this.state.str}  onChange={this.handlestr}></input>
           <br></br>
           <br></br>
           <button onClick={this.searchHandler}>search</button>
        </div>
    )
}

}

