import React, { Component } from 'react';
import './Accidentform.css'


class Trafficform extends Component {



  constructor(props)
 {

super(props)

this.state={

  deviceId:20,
   location:'',
   addInfo:'',
   severityLevel:'',
   damages:'',
   incidentType:"Traffic",
   subtype:''

} 
this.handleLocation=this.handleLocation.bind(this)
this.damagesHandler=this.damagesHandler.bind(this)
this.severityHandler=this.severityHandler.bind(this)
this.handleAddinfo=this.handleAddinfo.bind(this)
this.submitHandler=this.submitHandler.bind(this)
this.subtypeHandler=this.subtypeHandler.bind(this)
    }


    handleLocation=(event)=>
    { 
        this.setState({  
        [event.target.name]: event.target.value
         })

    }

    handleAddinfo=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }


    damagesHandler=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }


    severityHandler=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }
    subtypeHandler=(event)=>
    { 
        this.setState({  
            subtype:event.target.value
         })

    }

    submitHandler=(event)=>
    {   var body={
      deviceId:this.state.deviceId,
      incidentType:this.state.incidentType,
      subtype:this.state.subtype,
      severityLevel:this.state.severityLevel,
      damages:this.state.damages,
      location:this.state.location,
      addInfo:this.state.addInfo,

    }
    event.preventDefault();
    alert("Thank you for reporting!")
    const url = "http://localhost:9000/incidents";
    console.log(body)
    let headers=new Headers();
    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');
    headers.append('Access-control-Allow-origin',url);
    headers.append('Access-control-Allow-Credentials','true');
    headers.append('POST','GET');
    fetch(url, {
      headers: headers,
      method: 'POST',
      body: JSON.stringify(body)
    })


      .then(response => response.json())
      .then(contents => {
        console.log(contents);


      })
      .catch(() => console.log("can't access" + url + "response. "))


}     

  render() {
    return (
        <div className='Container' >
          <br></br>
           <h3>Select Sub-Type</h3>
           <br></br>

                                            <input type="radio" id="Vehicles" name="subtype" value="vehicles" onChange={this.subtypeHandler}/>
                                              <label >Vehicles</label>
                                              <input type="radio" id="Processions" name="subtype" value="processions" onChange={this.subtypeHandler}/>
                                              <label >Processions</label>

                                              <br></br>



<br></br>


           <h3>Severity Level</h3>
           <br></br>
                                              <input  type="radio" id="Low" name="severityLevel" value="Low" onClick={this.severityHandler}/>
                                              <label >Low</label>
                                              <input type="radio" id="Medium" name="severityLevel" value="Medium" onClick={this.severityHandler}/>
                                              <label>Medium</label>
                                              <input type="radio" id="High" name="severityLevel" value="High" onClick={this.severityHandler}/>
                                              <label>High</label>
                                              <br></br>
                                              <br></br>
           <h3>Damages</h3>

              <br></br>
                                                <input  type="radio" id="Low" name="damages" value="Human" onClick={this.damagesHandler}/>
                                              <label >Human</label>
                                              <input type="radio" id="Medium" name="damages" value="Property" onClick={this.damagesHandler}/>
                                              <label >Property</label>

           <br></br>
           <br></br>
           <h3 >Location</h3>
           <br></br>
           <i className="fa-solid fa-street-view"></i>
           <input type="text" name="location" value={this.state.location} onChange={this.handleLocation} placeholder="Enter city or district"></input>
           <br></br>
           <br></br>
           <h3>Additonal Information</h3>
           <br></br>
           <i className="fa-solid fa-note-sticky"></i>
           <input type="text" name="addInfo" value={this.state.addInfo} onChange={this.handleAddinfo} placeholder="Description of incident"></input>
           <br></br>
           <br></br>  <br></br>
           <button onClick={this.submitHandler}>Submit</button>
           <br></br>
           <br></br>



          </div>                                        
    );
  }

}


export default Trafficform;
