import React from 'react'
import JSONDATA from './MOCK_DATA.json'
import './Search.css'
import {useState} from 'react' 
function Search() {
    const[setSearchTerm,searchTerm]=useState('')
  return (
    <div className="Search-container">
        <input type="text" 
        placeholder="Type in location ,date or type of incident" 
        onChange={event=>{setSearchTerm(event.target.value)}}>

        </input>
        {JSONDATA.filter((val)=>{
            if(val.time.includes(searchTerm)){
            return val.time
        }
        else if(val.Location.toLowerCase().includes(searchTerm.toLowerCase())){
            return val.Location
        }
        else{
            return null
        }
        }).
        map((val,key)=>{
            return(
            <div className="Location" key={key}>
                <p>{val.Location},{val.time}</p></div>
            )
        })}
    </div>
  )
}
export default Search
