       addInfo:"",
       incidentId:this.props.id
   }

   this.editHandler=this.editHandler.bind(this)

       }

       editHandler=(e)=>{ 
           const body={
               severity:this.state.severity,
               damages:this.state.damages,
               addInfo:this.state.addInfo, 
           }
