    headers.append('POST','GET','PUT');
    fetch(url, {
      headers: headers,
      method: 'POST',
      body:JSON.stringify(body)
    })
 alert("incident edited")

.catch((err)=>{
    console.log(err)
})
.then((response)=>response.json())
.then((json)=>console.log(json));


}     

updateSeverity(e){
    this.setState({
        incidentType: e.target.value 
      });
    }
updateDamages(e){
    this.setState({
        location: e.target.value 
        });
    }
updateInfo(e){
    this.setState({
        incidentType: e.target.value 
        });
    }


    render()
    {
        return(
            <div>Severity
                <input type="text" value="this.state.severity" onChange={this.updateSeverity}></input>
                Damages
                <input type="text" value="this.state.Damages" onChange={this.updateDamages}></input>
                Additonal Information
                <input type="text" value="this.state.addInfo" onChange={this.updateInfo}></input>
                <button onClick={this.editHandler}>Update</button>
            </div>
        )
    }
}

export default Edit
