import React,{useEffect,useState} from 'react'
import './Search.css'
export default function SearchFilter(){
    const[data,setData]=useState([])
    const[searchApiData,setSearchApiData]=useState([])
    const [filterVal,setFilterVal]=useState('')
    useEffect(()=>{
    const fetchData=()=>{
        fetch('https://jsonplaceholder.typicode.com/users')
            .then(response => response.json())
            .then(json => {
                    setData(json)
                    setSearchApiData(json) 
                })
    }
    fetchData();
},[])
const handleFilter=(e)=>
{
    if(e.target.value===''){
        setData(searchApiData)
    }
    else{
        const filterResult=searchApiData.filter(item =>item.name.toLowerCase().includes(e.target.value.toLowerCase())||item.username.toLowerCase().includes(e.target.value.toLowerCase()))
        if(filterResult.length>0){
        setData(filterResult)
        }
        else{
            setData([{"name":"No Records"}])
        }
    }
    setFilterVal(e.target.value)

}
function deleteUser(username){
    alert(username)
    fetch(`https://jsonplaceholder.typicode.com/users/$(username)`,{
        method:'DELETE'
    }).then((result)=>{
        result.json().then((resp)=>{
            console.warn(resp)
        })
    })
}
return(
    <div className='Search-container'>
        <div style={{margin:'20px 20%'}}>
            <h1>Incident Search</h1>
            <input type="search" placeholder='Search..' value={filterVal} onInput={(e)=>handleFilter(e)} />
        </div>
        <table>
            <th>Name</th>
            <th>User-Name</th>
            <th>Email</th>
            <th>Phone</th>
            <th>Operations</th>
            {
                data.map(item =>{
                    return(
                        <tr>
                            <td>{item.name}</td>
                            <td>{item.username}</td>
                            <td>{item.email}</td>
                            <td>{item.phone}</td>
                            <td><button onClick={()=>deleteUser(item.username)}>Delete</button></td>
                        </tr>
                    )
                })
            }
        </table>
    </div>
)
}
