import React, { Component } from 'react';


class Accidentform extends Component {



  constructor(props)
 {

super(props)

this.state={

   location:'',
   addinfo:'',
   severity:'',
   damages:'',
   incident:''

} 
this.handleLocation=this.handleLocation.bind(this)
this.damagesHandler=this.damagesHandler.bind(this)
this.severityHandler=this.severityHandler.bind(this)
this.incidentHandler=this.incidentHandler.bind(this)
this.handleAddinfo=this.handleAddinfo.bind(this)
this.submitHandler=this.submitHandler.bind(this)
this.subtypeHandler=this.subtypeHandler.bind(this)
    }


    handleLocation=(event)=>
    { 
        this.setState({  
        [event.target.name]: event.target.value
         })

    }

    handleAddinfo=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }


    damagesHandler=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }


    severityHandler=(event)=>
    { 
        this.setState({  
            [event.target.name]:event.target.value
         })

    }


    incidentHandler=(event)=>
    { 
        this.setState({  
            incident:event.target.value
         })

    }

    subtypeHandler=(event)=>
    { 
        this.setState({  
            subtype:event.target.value
         })

    }

  submitHandler=(event)=>
    {   

}       

  render() {
    return (
        <div >

   <label>Select incident type</label>
           <br></br>

                                            <input type="radio" id="Accident" name="incident" value="accident" onChange={this.incidentHandler}/>
                                              <label >Accident</label>
                                              <input type="radio" id="Traffic" name="incident" value="traffic" onChange={this.incidentHandler}/>
                                              <label >Traffic</label>
                                              <input type="radio" id="Hazard" name="incident" value="hazard" onChange={this.incidentHandler}/>
                                              <label >Hazard</label>
                                              <br></br>
           <br></br>
           <label>Select subtype</label>

           <br></br>

         <input type="radio" id="vr" name="subtype" value="Vehicle rollover" onChange={this.subtypeHandler}/>
           <label >Vehicle rollover</label>
           <input type="radio" id="hoc" name="subtype" value="Head on collision" onChange={this.subtypeHandler}/>
           <label >Head on collision</label>
           <input type="radio" id="dui" name="subtype" value="Driving under influence" onChange={this.subtypeHandler}/>
           <label >Driving under influence</label>
           <input type="radio" id="hr" name="subtype" value="Hit and run" onChange={this.subtypeHandler}/>
           <label >Hit and run</label>
           <input type="radio" id="ror" name="subtype" value="Run-off-road collision" onChange={this.subtypeHandler}/>
           <label >Run-off-road collision</label>
         <br></br>

           <label>severityLevel</label>
           <br></br>
                                              <input  type="radio" id="Low" name="severity" value="Low" onClick={this.severityHandler}/>
                                              <label >Low</label>
                                              <input type="radio" id="Medium" name="severity" value="Medium" onClick={this.severityHandler}/>
                                              <label>Medium</label>
                                              <input type="radio" id="High" name="severity" value="High" onClick={this.severityHandler}/>
                                              <label>High</label>
                                              <br></br>
                                              <br></br>
           <label>damages</label>

              <br></br>
                                                <input  type="radio" id="Low" name="damages" value="Human" onClick={this.damagesHandler}/>
                                              <label >Human</label>
                                              <input type="radio" id="Medium" name="damages" value="Property" onClick={this.damagesHandler}/>
                                              <label >Property</label>

           <br></br>
           <br></br>
           <label >Location</label>
           <br></br>
           <input type="text" name="location" value={this.state.location} onChange={this.handleLocation}></input>
           <br></br>
           <label>Additonal Information</label>
           <br></br>
           <input type="text" name="addinfo" value={this.state.addinfo} onChange={this.handleAddinfo}></input>
           <br></br>
           <br></br>  <br></br>
           <button onClick={this.submitHandler}>Submit</button>
           <br></br>
           <br></br>



          </div>                                        
    );
  }

}


export default Accidentform;
