import React from 'react';
import './Cards.css';
import CardItem from './CardItem';

function Cards() {
  return (
    <div className='cards'>
      <h1>Check out these EPIC Destinations!</h1>
      <div className="cards__container">
        <div className="cards__wrapper">
          <ul className="cards__items">
            <CardItem>
              <div className="form-inputs">
              <label htmlFor="Choose location" className="form-label">Location
                  <input type='text' name='Choose location' className='form-input' placeholder='district,city where incident occured'></input>
              </label>
          </div>
          <div className="form-inputs">
              <label htmlFor="Type of incident" className="form-label">Type of Incident
                  <input type='radio button' name='Choose location' className='form-input' placeholder='district,city where incident occured'></input>
              </label>
          </div>
          </CardItem>
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Cards;
