import React, { useState } from 'react';
import Cards from './Cards'
import Hazardform from './pages/Hazardform';
export default function Report (){
  const[active,setActive]=useState("")
    return (
        <div className="report">
              <button onClick={()=> setActive("")} >Accident</button>
              <button onClick={()=> setActive("2ndCard")}>Hazard</button>
              <button onClick={()=> setActive("3rdCard")}>Traffic</button>
          <div>
            {active===""&& <Cards title ="Accident"></Cards> }
            {active==="2ndCard"&& <Cards title ="Hazard"></Cards> }
            {active==="3rdCard"&& <Cards title ="Traffic"></Cards> }
          </div>
          </div>                                        
    )     
}
